import React, { Component } from 'react';

export default class MultiUpload extends Component {

    fileObj = [];
    fileArray = [];

    constructor(props) {
        super(props)
        this.state = {
            file: [null]
        }
        this.uploadMultipleFiles = this.uploadMultipleFiles.bind(this)
        this.uploadFiles = this.uploadFiles.bind(this)
    }

    uploadMultipleFiles(e) {
        this.fileObj.push(e.target.files[0])
        this.fileArray.push(URL.createObjectURL(e.target.files[0]))
        console.log(this.fileObj)
        this.setState({ file: this.fileArray })
    }

    uploadFiles(e) {
        e.preventDefault()
        console.log(this.state.file)
    }

    render() {
        return (
            <form>
               {(this.fileArray || []).map(url => (
                <div className="col-sm-12 row" style={{backgroundColor:'white',padding:'2px',margin:'2px'}}>
                    <div className="col-sm-4" >
                        <img src={url} alt="..." width="100px" />
                    </div>
                    <div className="col-sm-4" >
                    </div>
                    <div className="col-sm-4" style={{padding: '30px'}} >
                      <a onClick={()=>alert("hello")} className="btn btn-danger btn-sm"  style={{verticalAlign: 'middle'}}>
                        <i className="fa fa-trash" style={{color:'white'}}></i>
                      </a>
                    </div>
                </div>
               ))}
                <div className="form-group">
                    <input type="file" className="form-control" id="file" onChange={this.uploadMultipleFiles} _multiple  style={{display:'none'}}/>
                </div>
                <button type="button" className="btn btn-danger btn-block" onClick={()=>document.getElementById('file').click()}>Charger des photos</button>
            </form >
        )
    }
}
